import React, {useState} from 'react'

function NewUser(props) {
    const [name, setName] = useState('')
    const [age, setAge] = useState('')

    const nameChangeHandler = (event) => {
        if(event.target.value.trim().length > 0 ) {
        setName(event.target.value)
    }
}
    const ageChangeHandler = (event) => {
        if(event.target.value >0 && event.target.value < 120){
            setAge(event.target.value)
        }

    }
    const submitHandler = (event) => {
        event.preventDefault()
        props.addUser(name, age)
        setName('')
        setAge('')
    }
  return (
    <form onSubmit={submitHandler}>
        <label htmlFor="name">Name</label>
        <input type="text" id='name' name='name' value={name}  onChange={nameChangeHandler} />
        <label htmlFor="age">Age</label>
        <input type="number" id='age' name='age' value={age} onChange={ageChangeHandler} />
        <button>Submit</button>
    </form>
  )
}

export default NewUser
